import React, { useState } from "react";
import {
  View,
  TextInput,
  Button,
  Alert,
  StyleSheet,
  TouchableOpacity,
  Text,
  ActivityIndicator,
} from "react-native";
import { addVenda } from "../api";

export default function NewSaleScreen({ navigation }: any) {
  const [cliente, setCliente] = useState("");
  const [total, setTotal] = useState("");
  const [isSaving, setIsSaving] = useState(false);

  const salvarVenda = async () => {
    if (!cliente.trim() || !total.trim()) {
      Alert.alert("Atenção", "Preencha todos os campos obrigatórios!");
      return;
    }

    const valor = parseFloat(total);
    if (isNaN(valor) || valor <= 0) {
      Alert.alert("Erro", "Digite um valor válido para o total da venda.");
      return;
    }

    setIsSaving(true);

    try {
      console.log(">> Enviando venda:", { cliente_nome: cliente, total: valor });

      const res = await addVenda({ cliente_nome: cliente, total: valor });

      console.log(">> Resposta addVenda:", res);

      Alert.alert("Sucesso", "Venda registrada com sucesso!");
      navigation.goBack();
    } catch (error: any) {
      console.error("Erro ao registrar venda:", error);
      Alert.alert("Erro", error?.message || "Não foi possível registrar a venda");
    } finally {
      setIsSaving(false);
    }
  };

  return (
    <View style={styles.container}>
      <TextInput
        placeholder="Nome do Cliente"
        value={cliente}
        onChangeText={setCliente}
        style={styles.input}
        editable={!isSaving}
      />
      <TextInput
        placeholder="Valor total da venda"
        value={total}
        onChangeText={setTotal}
        keyboardType="decimal-pad"
        style={styles.input}
        editable={!isSaving}
      />

      <TouchableOpacity
        style={[styles.saveButton, isSaving && { opacity: 0.6 }]}
        onPress={salvarVenda}
        disabled={isSaving}
      >
        {isSaving ? (
          <ActivityIndicator color="#fff" />
        ) : (
          <Text style={styles.saveButtonText}>Registrar Venda</Text>
        )}
      </TouchableOpacity>

      <TouchableOpacity
        style={styles.backButton}
        onPress={() => navigation.goBack()}
      >
        <Text style={styles.backButtonText}>⬅ Voltar para Tela Inicial</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: "#f5f5f5",
    justifyContent: "center",
  },
  input: {
    borderBottomWidth: 1,
    borderBottomColor: "#ccc",
    marginBottom: 15,
    paddingVertical: 8,
    fontSize: 16,
  },
  saveButton: {
    backgroundColor: "#4CAF50",
    padding: 15,
    borderRadius: 8,
    alignItems: "center",
    marginBottom: 15,
  },
  saveButtonText: {
    color: "#fff",
    fontSize: 16,
    fontWeight: "bold",
  },
  backButton: {
    padding: 12,
    borderRadius: 8,
    backgroundColor: "#ddd",
    alignItems: "center",
  },
  backButtonText: {
    fontSize: 16,
    color: "#333",
  },
});
