import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
  RefreshControl,
} from 'react-native';

interface DashboardData {
  vendas_mes: number;
  total_produtos: number;
  lucro_mes: number;
  vendas_pendentes: number;
}

export default function DashboardScreen({ navigation }: any) {
  const [data, setData] = useState<DashboardData>({
    vendas_mes: 0,
    total_produtos: 0,
    lucro_mes: 0,
    vendas_pendentes: 0,
  });
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);

  const loadDashboardData = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/dashboard');
      const result = await response.json();
      
      if (result.success) {
        setData(result.data);
      } else {
        throw new Error(result.error || 'Erro ao carregar dados');
      }
    } catch (error) {
      console.error('Erro no dashboard:', error);
      Alert.alert('Erro', 'Não foi possível carregar os dados do dashboard');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  useEffect(() => {
    loadDashboardData();
  }, []);

  const onRefresh = () => {
    setRefreshing(true);
    loadDashboardData();
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(value);
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#2196F3" />
        <Text style={styles.loadingText}>Carregando dashboard...</Text>
      </View>
    );
  }

  return (
    <ScrollView 
      style={styles.container}
      refreshControl={
        <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
      }
    >
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.title}>GEV App</Text>
        <Text style={styles.subtitle}>Gestão Empresarial de Vendas</Text>
        
        {__DEV__ && (
          <TouchableOpacity 
            style={styles.debugButton}
            onPress={() => Alert.alert('Debug', 'Sistema de debug ativo')}
          >
            <Text style={styles.debugButtonText}>🔧</Text>
          </TouchableOpacity>
        )}
      </View>

      {/* Cards de métricas */}
      <View style={styles.cardsContainer}>
        <View style={styles.row}>
          <TouchableOpacity style={[styles.card, styles.cardPrimary]}>
            <Text style={styles.cardValue}>
              {formatCurrency(data.vendas_mes)}
            </Text>
            <Text style={styles.cardLabel}>Vendas do Mês</Text>
          </TouchableOpacity>

          <TouchableOpacity style={[styles.card, styles.cardSuccess]}>
            <Text style={styles.cardValue}>
              {formatCurrency(data.lucro_mes)}
            </Text>
            <Text style={styles.cardLabel}>Lucro do Mês</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.row}>
          <TouchableOpacity style={[styles.card, styles.cardInfo]}>
            <Text style={styles.cardValue}>{data.total_produtos}</Text>
            <Text style={styles.cardLabel}>Produtos Cadastrados</Text>
          </TouchableOpacity>

          <TouchableOpacity style={[styles.card, styles.cardWarning]}>
            <Text style={styles.cardValue}>{data.vendas_pendentes}</Text>
            <Text style={styles.cardLabel}>Vendas Pendentes</Text>
          </TouchableOpacity>
        </View>
      </View>

      {/* Menu principal */}
      <View style={styles.menuContainer}>
        <Text style={styles.menuTitle}>Menu Principal</Text>
        
        <View style={styles.menuGrid}>
          <TouchableOpacity 
            style={styles.menuItem}
            onPress={() => navigation.navigate('Products')}
          >
            <Text style={styles.menuIcon}>📦</Text>
            <Text style={styles.menuText}>Produtos</Text>
            <Text style={styles.menuSubtext}>Gerenciar estoque</Text>
          </TouchableOpacity>

          <TouchableOpacity 
            style={styles.menuItem}
            onPress={() => navigation.navigate('NewSale')}
          >
            <Text style={styles.menuIcon}>💰</Text>
            <Text style={styles.menuText}>Nova Venda</Text>
            <Text style={styles.menuSubtext}>Registrar venda</Text>
          </TouchableOpacity>

          <TouchableOpacity 
            style={styles.menuItem}
            onPress={() => navigation.navigate('SalesHistory')}
          >
            <Text style={styles.menuIcon}>📊</Text>
            <Text style={styles.menuText}>Histórico</Text>
            <Text style={styles.menuSubtext}>Ver vendas</Text>
          </TouchableOpacity>

          <TouchableOpacity 
            style={styles.menuItem}
            onPress={() => navigation.navigate('Reports')}
          >
            <Text style={styles.menuIcon}>📈</Text>
            <Text style={styles.menuText}>Relatórios</Text>
            <Text style={styles.menuSubtext}>Análises</Text>
          </TouchableOpacity>
        </View>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f5f5f5',
  },
  loadingText: {
    marginTop: 10,
    color: '#666',
    fontSize: 16,
  },
  header: {
    backgroundColor: '#2196F3',
    paddingTop: 50,
    paddingBottom: 30,
    paddingHorizontal: 20,
    alignItems: 'center',
    position: 'relative',
  },
  debugButton: {
    position: 'absolute',
    right: 20,
    top: 55,
    width: 30,
    height: 30,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(255,255,255,0.2)',
    borderRadius: 15,
  },
  debugButtonText: {
    fontSize: 16,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 5,
  },
  subtitle: {
    fontSize: 16,
    color: 'white',
    opacity: 0.9,
  },
  cardsContainer: {
    padding: 20,
  },
  row: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 15,
  },
  card: {
    width: '48%',
    padding: 20,
    borderRadius: 12,
    elevation: 3,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
  },
  cardPrimary: {
    backgroundColor: '#2196F3',
  },
  cardSuccess: {
    backgroundColor: '#4CAF50',
  },
  cardInfo: {
    backgroundColor: '#FF9800',
  },
  cardWarning: {
    backgroundColor: '#F44336',
  },
  cardValue: {
    fontSize: 18,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 5,
  },
  cardLabel: {
    fontSize: 12,
    color: 'white',
    opacity: 0.9,
  },
  menuContainer: {
    padding: 20,
    paddingTop: 10,
  },
  menuTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#333',
    marginBottom: 20,
  },
  menuGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  },
  menuItem: {
    width: '48%',
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 12,
    alignItems: 'center',
    marginBottom: 15,
    elevation: 2,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.1,
    shadowRadius: 2,
  },
  menuIcon: {
    fontSize: 32,
    marginBottom: 10,
  },
  menuText: {
    fontSize: 16,
    fontWeight: '600',
    color: '#333',
    marginBottom: 5,
  },
  menuSubtext: {
    fontSize: 12,
    color: '#666',
    textAlign: 'center',
  },
});
