import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
  RefreshControl,
} from 'react-native';
import { useFocusEffect } from '@react-navigation/native';
import { VictoryBar, VictoryChart, VictoryTheme } from 'victory-native';

interface DashboardData {
  vendas_mes: number;
  total_produtos: number;
  lucro_mes: number;
  vendas_pendentes: number;
}

export default function DashboardScreen({ navigation }: any) {
  const [data, setData] = useState<DashboardData>({
    vendas_mes: 0,
    total_produtos: 0,
    lucro_mes: 0,
    vendas_pendentes: 0,
  });
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);

  const loadDashboardData = async (showLoading = true) => {
    try {
      if (showLoading) setLoading(true);

      console.log('üìä Carregando dados do dashboard...');

      const statusResponse = await fetch('http://localhost:3000/api/status');
      if (!statusResponse.ok) throw new Error('Backend n√£o est√° respondendo');

      const response = await fetch('http://localhost:3000/api/dashboard/summary');
      if (!response.ok) throw new Error(`HTTP ${response.status}: ${response.statusText}`);

      const result = await response.json();
      console.log('üìä Dados do dashboard recebidos:', result);

      setData({
        vendas_mes: result.vendasMes || 0,
        total_produtos: result.produtos || 0,
        lucro_mes: result.lucroMes || 0,
        vendas_pendentes: result.pendentes || 0,
      });
    } catch (error: any) {
      console.error('‚ùå Erro no dashboard:', error);
      Alert.alert(
        'Erro de Conex√£o',
        'N√£o foi poss√≠vel carregar os dados do dashboard.\n' + error.message,
        [{ text: 'Tentar Novamente', onPress: () => loadDashboardData() }, { text: 'OK' }]
      );
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  useFocusEffect(
    React.useCallback(() => {
      loadDashboardData();
    }, [])
  );

  const onRefresh = () => {
    setRefreshing(true);
    loadDashboardData(false);
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(value);
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#2196F3" />
        <Text style={styles.loadingText}>Carregando dashboard...</Text>
      </View>
    );
  }

  return (
    <ScrollView
      style={styles.container}
      refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
    >
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.title}>GEV App</Text>
        <Text style={styles.subtitle}>Gest√£o Empresarial de Vendas</Text>
      </View>

      {/* Cards de m√©tricas */}
      <View style={styles.cardsContainer}>
        <View style={styles.row}>
          <View style={[styles.card, styles.cardPrimary]}>
            <Text style={styles.cardValue}>{formatCurrency(data.vendas_mes)}</Text>
            <Text style={styles.cardLabel}>Vendas do M√™s</Text>
          </View>
          <View style={[styles.card, styles.cardSuccess]}>
            <Text style={styles.cardValue}>{formatCurrency(data.lucro_mes)}</Text>
            <Text style={styles.cardLabel}>Lucro do M√™s</Text>
          </View>
        </View>
        <View style={styles.row}>
          <View style={[styles.card, styles.cardInfo]}>
            <Text style={styles.cardValue}>{data.total_produtos}</Text>
            <Text style={styles.cardLabel}>Produtos Cadastrados</Text>
          </View>
          <View style={[styles.card, styles.cardWarning]}>
            <Text style={styles.cardValue}>{data.vendas_pendentes}</Text>
            <Text style={styles.cardLabel}>Vendas Pendentes</Text>
          </View>
        </View>
      </View>

      {/* Gr√°fico */}
      <View style={styles.chartContainer}>
        <Text style={styles.chartTitle}>Vendas vs Lucro</Text>
        <VictoryChart theme={VictoryTheme.material} domainPadding={20}>
          <VictoryBar
            data={[
              { x: 'Vendas', y: data.vendas_mes },
              { x: 'Lucro', y: data.lucro_mes },
            ]}
            style={{ data: { fill: '#2196F3' } }}
          />
        </VictoryChart>
      </View>

      {/* Menu principal */}
      <View style={styles.menuContainer}>
        <Text style={styles.menuTitle}>Menu Principal</Text>
        <View style={styles.menuGrid}>
          <TouchableOpacity style={styles.menuItem} onPress={() => navigation.navigate('Products')}>
            <Text style={styles.menuIcon}>üì¶</Text>
            <Text style={styles.menuText}>Produtos</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.menuItem} onPress={() => navigation.navigate('NewSale')}>
            <Text style={styles.menuIcon}>üí∞</Text>
            <Text style={styles.menuText}>Nova Venda</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.menuItem} onPress={() => navigation.navigate('SalesHistory')}>
            <Text style={styles.menuIcon}>üìä</Text>
            <Text style={styles.menuText}>Hist√≥rico</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.menuItem} onPress={() => navigation.navigate('Reports')}>
            <Text style={styles.menuIcon}>üìà</Text>
            <Text style={styles.menuText}>Relat√≥rios</Text>
          </TouchableOpacity>
        </View>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#f5f5f5' },
  loadingContainer: { flex: 1, justifyContent: 'center', alignItems: 'center' },
  loadingText: { marginTop: 10, fontSize: 16, color: '#666' },
  header: { backgroundColor: '#2196F3', paddingTop: 50, paddingBottom: 30, alignItems: 'center' },
  title: { fontSize: 28, fontWeight: 'bold', color: 'white' },
  subtitle: { fontSize: 16, color: 'white', opacity: 0.9 },
  cardsContainer: { padding: 20 },
  row: { flexDirection: 'row', justifyContent: 'space-between', marginBottom: 15 },
  card: { width: '48%', padding: 20, borderRadius: 12, elevation: 3 },
  cardPrimary: { backgroundColor: '#2196F3' },
  cardSuccess: { backgroundColor: '#4CAF50' },
  cardInfo: { backgroundColor: '#FF9800' },
  cardWarning: { backgroundColor: '#F44336' },
  cardValue: { fontSize: 18, fontWeight: 'bold', color: 'white', marginBottom: 5 },
  cardLabel: { fontSize: 12, color: 'white', opacity: 0.9 },
  chartContainer: { padding: 20 },
  chartTitle: { fontSize: 16, fontWeight: 'bold', marginBottom: 10 },
  menuContainer: { padding: 20, paddingTop: 10 },
  menuTitle: { fontSize: 20, fontWeight: 'bold', color: '#333', marginBottom: 20 },
  menuGrid: { flexDirection: 'row', flexWrap: 'wrap', justifyContent: 'space-between' },
  menuItem: {
    width: '48%',
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 12,
    alignItems: 'center',
    marginBottom: 15,
  },
  menuIcon: { fontSize: 32, marginBottom: 10 },
  menuText: { fontSize: 16, fontWeight: '600', color: '#333' },
});
