import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
  RefreshControl,
} from 'react-native';
import { useFocusEffect } from '@react-navigation/native';
import { LineChart } from 'react-native-chart-kit';
import { Dimensions } from 'react-native';

interface DashboardData {
  vendas_mes: number;
  total_produtos: number;
  lucro_mes: number;
  vendas_pendentes: number;
}

interface HistoricoData {
  mes: string;
  total_vendas: number;
  lucro: number;
}

export default function DashboardScreen({ navigation }: any) {
  const [data, setData] = useState<DashboardData>({
    vendas_mes: 0,
    total_produtos: 0,
    lucro_mes: 0,
    vendas_pendentes: 0,
  });
  const [historico, setHistorico] = useState<HistoricoData[]>([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [reloading, setReloading] = useState(false);

  const loadDashboardData = async (showLoading = true) => {
    try {
      if (showLoading) setLoading(true);
      setReloading(true);

      const response = await fetch('http://localhost:3000/api/dashboard/summary');
      if (!response.ok) throw new Error(`HTTP ${response.status}`);

      const result = await response.json();
      setData({
        vendas_mes: result.vendasMes,
        total_produtos: result.produtos,
        lucro_mes: result.lucroMes,
        vendas_pendentes: result.pendentes,
      });

      const histResponse = await fetch('http://localhost:3000/api/dashboard/historico');
      if (!histResponse.ok) throw new Error(`HTTP ${histResponse.status}`);
      const histResult = await histResponse.json();
      setHistorico(histResult);

    } catch (error: any) {
      console.error('Erro ao carregar dashboard:', error);
      Alert.alert(
        'Erro de ConexÃ£o',
        'NÃ£o foi possÃ­vel carregar os dados do dashboard.\n\nErro: ' + error.message
      );
    } finally {
      if (showLoading) setLoading(false);
      setRefreshing(false);
      setReloading(false);
    }
  };

  useFocusEffect(
    React.useCallback(() => {
      loadDashboardData();
    }, [])
  );

  const onRefresh = () => {
    setRefreshing(true);
    loadDashboardData(false);
  };

  const formatCurrency = (value: number) =>
    new Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(value);

  // Preparar dados do grÃ¡fico
  const chartLabels = historico.map(h => h.mes);
  const chartVendas = historico.map(h => h.total_vendas);
  const chartLucro = historico.map(h => h.lucro);

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#2196F3" />
        <Text style={styles.loadingText}>Carregando dashboard...</Text>
      </View>
    );
  }

  return (
    <ScrollView
      style={styles.container}
      refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
    >
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.title}>GEV App</Text>
        <Text style={styles.subtitle}>GestÃ£o Empresarial de Vendas</Text>

        {/* BotÃ£o recarregar profissional */}
        <TouchableOpacity
          style={styles.reloadButton}
          onPress={() => loadDashboardData()}
          disabled={reloading}
        >
          {reloading ? (
            <ActivityIndicator size="small" color="#fff" />
          ) : (
            <Text style={styles.reloadText}>ðŸ”„ Atualizar</Text>
          )}
        </TouchableOpacity>
      </View>

      {/* Cards */}
      <View style={styles.cardsContainer}>
        <View style={styles.row}>
          <View style={[styles.card, styles.cardPrimary]}>
            <Text style={styles.cardValue}>{formatCurrency(data.vendas_mes)}</Text>
            <Text style={styles.cardLabel}>Vendas do MÃªs</Text>
          </View>
          <View style={[styles.card, styles.cardSuccess]}>
            <Text style={styles.cardValue}>{formatCurrency(data.lucro_mes)}</Text>
            <Text style={styles.cardLabel}>Lucro do MÃªs</Text>
          </View>
        </View>
        <View style={styles.row}>
          <View style={[styles.card, styles.cardInfo]}>
            <Text style={styles.cardValue}>{data.total_produtos}</Text>
            <Text style={styles.cardLabel}>Produtos Cadastrados</Text>
          </View>
          <View style={[styles.card, styles.cardWarning]}>
            <Text style={styles.cardValue}>{data.vendas_pendentes}</Text>
            <Text style={styles.cardLabel}>Vendas Pendentes</Text>
          </View>
        </View>
      </View>

      {/* GrÃ¡fico Vendas x Lucro */}
      <View style={styles.chartContainer}>
        <Text style={styles.chartTitle}>HistÃ³rico Vendas vs Lucro</Text>
        {historico.length > 0 ? (
          <LineChart
            data={{
              labels: chartLabels,
              datasets: [
                { data: chartVendas, color: () => '#2196F3', strokeWidth: 2},
                { data: chartLucro, color: () => '#4CAF50', strokeWidth: 2},
              ],
            }}
            width={Dimensions.get('window').width - 40}
            height={220}
            yAxisLabel="R$ "
            chartConfig={{
              backgroundColor: '#ffffff',
              backgroundGradientFrom: '#f5f5f5',
              backgroundGradientTo: '#f5f5f5',
              decimalPlaces: 2,
              color: (opacity = 1) => `rgba(33, 150, 243, ${opacity})`,
              labelColor: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
              style: { borderRadius: 16 },
              propsForDots: { r: '4', strokeWidth: '2', stroke: '#2196F3' },
            }}
            bezier
            style={{ marginVertical: 8, borderRadius: 16 }}
          />
        ) : (
          <Text style={{ textAlign: 'center', color: '#666' }}>Sem dados para exibir.</Text>
        )}
      </View>

      {/* Menu */}
      <View style={styles.menuContainer}>
        <Text style={styles.menuTitle}>Menu Principal</Text>
        <View style={styles.menuGrid}>
          <TouchableOpacity
            style={styles.menuItem}
            onPress={() => navigation.navigate('Products')}
          >
            <Text style={styles.menuIcon}>ðŸ“¦</Text>
            <Text style={styles.menuText}>Produtos</Text>
            <Text style={styles.menuSubtext}>Gerenciar estoque</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.menuItem}
            onPress={() => navigation.navigate('NewSale')}
          >
            <Text style={styles.menuIcon}>ðŸ’°</Text>
            <Text style={styles.menuText}>Nova Venda</Text>
            <Text style={styles.menuSubtext}>Registrar venda</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.menuItem}
            onPress={() => navigation.navigate('SalesHistory')}
          >
            <Text style={styles.menuIcon}>ðŸ“Š</Text>
            <Text style={styles.menuText}>HistÃ³rico</Text>
            <Text style={styles.menuSubtext}>Ver vendas</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.menuItem}
            onPress={() => navigation.navigate('Reports')}
          >
            <Text style={styles.menuIcon}>ðŸ“ˆ</Text>
            <Text style={styles.menuText}>RelatÃ³rios</Text>
            <Text style={styles.menuSubtext}>AnÃ¡lises</Text>
          </TouchableOpacity>
        </View>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#f5f5f5' },
  loadingContainer: { flex: 1, justifyContent: 'center', alignItems: 'center', backgroundColor: '#f5f5f5' },
  loadingText: { marginTop: 10, color: '#666', fontSize: 16 },
  header: { backgroundColor: '#2196F3', paddingTop: 50, paddingBottom: 30, paddingHorizontal: 20, alignItems: 'center', position: 'relative' },
  title: { fontSize: 28, fontWeight: 'bold', color: 'white', marginBottom: 5 },
  subtitle: { fontSize: 16, color: 'white', opacity: 0.9 },
  reloadButton: { position: 'absolute', right: 20, top: 55, padding: 8, backgroundColor: 'rgba(255,255,255,0.2)', borderRadius: 20 },
  reloadText: { color: '#fff', fontWeight: 'bold' },
  cardsContainer: { padding: 20 },
  row: { flexDirection: 'row', justifyContent: 'space-between', marginBottom: 15 },
  card: { width: '48%', padding: 20, borderRadius: 12, elevation: 3, shadowColor: '#000', shadowOffset: { width: 0, height: 2 }, shadowOpacity: 0.1, shadowRadius: 4 },
  cardPrimary: { backgroundColor: '#2196F3' },
  cardSuccess: { backgroundColor: '#4CAF50' },
  cardInfo: { backgroundColor: '#FF9800' },
  cardWarning: { backgroundColor: '#F44336' },
  cardValue: { fontSize: 18, fontWeight: 'bold', color: 'white', marginBottom: 5 },
  cardLabel: { fontSize: 12, color: 'white', opacity: 0.9 },
  chartContainer: { paddingHorizontal: 20, paddingBottom: 20 },
  chartTitle: { fontSize: 16, fontWeight: 'bold', marginBottom: 8, color: '#333' },
  menuContainer: { padding: 20, paddingTop: 10 },
  menuTitle: { fontSize: 20, fontWeight: 'bold', color: '#333', marginBottom: 20 },
  menuGrid: { flexDirection: 'row', flexWrap: 'wrap', justifyContent: 'space-between' },
  menuItem: { width: '48%', backgroundColor: 'white', padding: 20, borderRadius: 12, alignItems: 'center', marginBottom: 15, elevation: 2, shadowColor: '#000', shadowOffset: { width: 0, height: 1 }, shadowOpacity: 0.1, shadowRadius: 2 },
  menuIcon: { fontSize: 32, marginBottom: 10 },
  menuText: { fontSize: 16, fontWeight: '600', color: '#333', marginBottom: 5 },
  menuSubtext: { fontSize: 12, color: '#666', textAlign: 'center' },
});
