import React, { useState } from "react";
import { View, TextInput, Button, Alert, Image, ScrollView, ActivityIndicator } from "react-native";
import * as ImagePicker from "expo-image-picker";
import { addProduto } from "../api"; // Certifique-se que essa função usa fetch para enviar FormData

export default function NewProductScreen({ navigation }: any) {
  const [nome, setNome] = useState("");
  const [precoCusto, setPrecoCusto] = useState("");
  const [precoVenda, setPrecoVenda] = useState("");
  const [observacoes, setObservacoes] = useState("");
  const [estoqueAtual, setEstoqueAtual] = useState("");
  const [imagem, setImagem] = useState<any>(null);
  const [loading, setLoading] = useState(false);

  // Selecionar imagem do celular
  const escolherImagem = async () => {
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      quality: 1,
    });

    if (!result.canceled && result.assets.length > 0) {
      setImagem(result.assets[0]);
    }
  };

  // Salvar produto
  const salvarProduto = async () => {
    if (!nome || !precoCusto || !precoVenda) {
      Alert.alert("Atenção", "Preencha todos os campos obrigatórios!");
      return;
    }

    setLoading(true);

    const formData = new FormData();
    formData.append("nome", nome);
    formData.append("preco_custo", parseFloat(precoCusto).toString());
    formData.append("preco_venda", parseFloat(precoVenda).toString());
    formData.append("observacoes", observacoes || "");
    formData.append("estoque_atual", estoqueAtual ? parseInt(estoqueAtual).toString() : "0");

    if (imagem) {
      formData.append("imagem", {
        uri: imagem.uri.startsWith("file://") ? imagem.uri : "file://" + imagem.uri,
        type: "image/jpeg", // ou "image/png" se for o caso
        name: imagem.uri.split("/").pop() || "produto.jpg",
      } as unknown as Blob);
    }

    try {
      const response = await addProduto(formData); // Deve enviar POST para /api/produtos
      console.log("Resposta do servidor:", response.data);

      Alert.alert("Sucesso", "Produto cadastrado com sucesso!");

      // Resetar campos
      setNome("");
      setPrecoCusto("");
      setPrecoVenda("");
      setObservacoes("");
      setEstoqueAtual("");
      setImagem(null);

      // Voltar para lista de produtos
      navigation.goBack();
    } catch (error: any) {
      console.error("Erro ao salvar produto:", error);
      Alert.alert("Erro", "Não foi possível cadastrar o produto");
    } finally {
      setLoading(false);
    }
  };

  return (
    <ScrollView style={{ padding: 20 }}>
      <TextInput
        placeholder="Nome do produto"
        value={nome}
        onChangeText={setNome}
        style={{ marginBottom: 10, borderBottomWidth: 1, paddingVertical: 5 }}
      />
      <TextInput
        placeholder="Preço de custo"
        value={precoCusto}
        onChangeText={setPrecoCusto}
        keyboardType="numeric"
        style={{ marginBottom: 10, borderBottomWidth: 1, paddingVertical: 5 }}
      />
      <TextInput
        placeholder="Preço de venda"
        value={precoVenda}
        onChangeText={setPrecoVenda}
        keyboardType="numeric"
        style={{ marginBottom: 10, borderBottomWidth: 1, paddingVertical: 5 }}
      />
      <TextInput
        placeholder="Observações"
        value={observacoes}
        onChangeText={setObservacoes}
        style={{ marginBottom: 10, borderBottomWidth: 1, paddingVertical: 5 }}
      />
      <TextInput
        placeholder="Estoque atual"
        value={estoqueAtual}
        onChangeText={setEstoqueAtual}
        keyboardType="numeric"
        style={{ marginBottom: 10, borderBottomWidth: 1, paddingVertical: 5 }}
      />

      <Button title="Escolher Imagem" onPress={escolherImagem} />

      {imagem && (
        <Image
          source={{ uri: imagem.uri }}
          style={{ width: 150, height: 150, marginVertical: 10, borderRadius: 8 }}
        />
      )}

      {loading ? (
        <ActivityIndicator size="large" color="#000" style={{ marginVertical: 10 }} />
      ) : (
        <Button title="Salvar Produto" onPress={salvarProduto} />
      )}
    <View style ={{ marginTop:10}}>
      <Button title="← Voltar" onPress={() => navigation.goBack()}  />
    </View>
    </ScrollView>
  );
}
