import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Alert,
  ActivityIndicator,
  KeyboardAvoidingView,
  Platform,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

interface LoginScreenProps {
  navigation: any;
}

export default function LoginScreen({ navigation }: LoginScreenProps) {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [loading, setLoading] = useState(false);

  const validarCampos = () => {
    if (!email.trim()) {
      Alert.alert('Aten√ß√£o', 'Por favor, digite seu email');
      return false;
    }
    
    if (!email.includes('@')) {
      Alert.alert('Aten√ß√£o', 'Por favor, digite um email v√°lido');
      return false;
    }
    
    if (!senha.trim()) {
      Alert.alert('Aten√ß√£o', 'Por favor, digite sua senha');
      return false;
    }
    
    if (senha.length < 6) {
      Alert.alert('Aten√ß√£o', 'A senha deve ter pelo menos 6 caracteres');
      return false;
    }
    
    return true;
  };

  const fazerLogin = async () => {
    if (!validarCampos()) {
      return;
    }

    setLoading(true);

    try {
      // Simular autentica√ß√£o (substitua por sua API de autentica√ß√£o real)
      const usuariosValidos = [
        { email: 'admin@gevapp.com', senha: '123456' },
        { email: 'usuario@gevapp.com', senha: 'gevapp123' },
        { email: 'demo@gevapp.com', senha: 'demo123' }
      ];

      // Verificar se o usu√°rio existe
      const usuarioEncontrado = usuariosValidos.find(
        user => user.email.toLowerCase() === email.toLowerCase() && user.senha === senha
      );

      if (!usuarioEncontrado) {
        Alert.alert(
          'Login Inv√°lido',
          'Email ou senha incorretos.\n\nUsu√°rios de teste:\n‚Ä¢ admin@gevapp.com / 123456\n‚Ä¢ demo@gevapp.com / demo123'
        );
        return;
      }

      // Salvar dados do usu√°rio logado
      await AsyncStorage.setItem('userLoggedIn', 'true');
      await AsyncStorage.setItem('userEmail', email);
      await AsyncStorage.setItem('loginTime', new Date().toISOString());

      Alert.alert('Sucesso!', 'Login realizado com sucesso!', [
        {
          text: 'OK',
          onPress: () => navigation.replace('Dashboard')
        }
      ]);

    } catch (error) {
      console.error('Erro no login:', error);
      Alert.alert('Erro', 'Ocorreu um erro ao fazer login. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <KeyboardAvoidingView 
      style={styles.container} 
      behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
    >
      <View style={styles.content}>
        {/* Logo/Header */}
        <View style={styles.header}>
          <Text style={styles.logo}>üì±</Text>
          <Text style={styles.appName}>GEV App</Text>
          <Text style={styles.appSubtitle}>Gest√£o Empresarial de Vendas</Text>
        </View>

        {/* Formul√°rio */}
        <View style={styles.form}>
          <Text style={styles.formTitle}>Entrar na sua conta</Text>
          
          <View style={styles.inputGroup}>
            <Text style={styles.label}>Email</Text>
            <TextInput
              style={styles.input}
              placeholder="Digite seu email"
              value={email}
              onChangeText={setEmail}
              keyboardType="email-address"
              autoCapitalize="none"
              autoCorrect={false}
              placeholderTextColor="#999"
            />
          </View>

          <View style={styles.inputGroup}>
            <Text style={styles.label}>Senha</Text>
            <TextInput
              style={styles.input}
              placeholder="Digite sua senha"
              value={senha}
              onChangeText={setSenha}
              secureTextEntry
              placeholderTextColor="#999"
            />
          </View>

          <TouchableOpacity
            style={[styles.loginButton, loading && styles.loginButtonDisabled]}
            onPress={fazerLogin}
            disabled={loading}
          >
            {loading ? (
              <ActivityIndicator size="small" color="#fff" />
            ) : (
              <Text style={styles.loginButtonText}>Entrar</Text>
            )}
          </TouchableOpacity>

          {/* Informa√ß√µes de teste */}
          <View style={styles.testInfo}>
            <Text style={styles.testInfoTitle}>Contas de teste:</Text>
            <Text style={styles.testInfoText}>‚Ä¢ admin@gevapp.com / 123456</Text>
            <Text style={styles.testInfoText}>‚Ä¢ demo@gevapp.com / demo123</Text>
          </View>
        </View>

        {/* Link para cadastro */}
        <TouchableOpacity 
          style={styles.registerLink}
          onPress={() => navigation.navigate('Register')}
        >
          <Text style={styles.registerLinkText}>
            N√£o tem conta? Cadastre-se aqui
          </Text>
        </TouchableOpacity>
      </View>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
  },
  content: {
    flex: 1,
    justifyContent: 'center',
    paddingHorizontal: 30,
  },
  header: {
    alignItems: 'center',
    marginBottom: 40,
  },
  logo: {
    fontSize: 60,
    marginBottom: 10,
  },
  appName: {
    fontSize: 32,
    fontWeight: 'bold',
    color: '#2196F3',
    marginBottom: 5,
  },
  appSubtitle: {
    fontSize: 16,
    color: '#666',
    textAlign: 'center',
  },
  form: {
    backgroundColor: 'white',
    borderRadius: 16,
    padding: 30,
    elevation: 4,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 8,
  },
  formTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#333',
    textAlign: 'center',
    marginBottom: 25,
  },
  inputGroup: {
    marginBottom: 20,
  },
  label: {
    fontSize: 16,
    fontWeight: '600',
    color: '#333',
    marginBottom: 8,
  },
  input: {
    backgroundColor: '#f8f9fa',
    borderRadius: 10,
    paddingHorizontal: 15,
    paddingVertical: 12,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#e9ecef',
  },
  loginButton: {
    backgroundColor: '#2196F3',
    borderRadius: 10,
    paddingVertical: 15,
    alignItems: 'center',
    marginTop: 10,
  },
  loginButtonDisabled: {
    backgroundColor: '#cccccc',
  },
  loginButtonText: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
  },
  testInfo: {
    marginTop: 20,
    paddingTop: 20,
    borderTopWidth: 1,
    borderTopColor: '#eee',
  },
  testInfoTitle: {
    fontSize: 14,
    fontWeight: '600',
    color: '#666',
    marginBottom: 8,
  },
  testInfoText: {
    fontSize: 12,
    color: '#888',
    marginBottom: 3,
  },
  registerLink: {
    alignItems: 'center',
    marginTop: 30,
  },
  registerLinkText: {
    fontSize: 16,
    color: '#2196F3',
    fontWeight: '600',
  },
});
