import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  ScrollView,
  StyleSheet,
  Alert,
  ActivityIndicator,
  Image,
  Platform,
} from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import { useRoute, RouteProp } from '@react-navigation/native';

type RootStackParamList = {
  NewProduct: { produto?: any } | undefined;
};

type NewProductScreenRouteProp = RouteProp<RootStackParamList, 'NewProduct'>;

export default function NewProductScreen({ navigation }: any) {
  const [nome, setNome] = useState('');
  const [precoCusto, setPrecoCusto] = useState('');
  const [precoVenda, setPrecoVenda] = useState('');
  const [observacoes, setObservacoes] = useState('');
  const [estoqueAtual, setEstoqueAtual] = useState('');
  const [imagem, setImagem] = useState<any>(null);
  const [imagemExistente, setImagemExistente] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [margemLucro, setMargemLucro] = useState('0');

  const route = useRoute<NewProductScreenRouteProp>();
  const produto = route.params?.produto;
  const isEditing = !!produto;

  useEffect(() => {
    if (isEditing && produto) {
      setNome(produto.nome || '');
      setPrecoCusto(produto.preco_custo?.toString() || '');
      setPrecoVenda(produto.preco_venda?.toString() || '');
      setObservacoes(produto.observacoes || '');
      setEstoqueAtual(produto.estoque_atual?.toString() || '0');
      setImagemExistente(produto.imagem || null);
      setMargemLucro(produto.margem_lucro?.toString() || '0');
    }
  }, [isEditing, produto]);

  useEffect(() => {
    if (precoCusto && precoVenda) {
      const custo = parseFloat(precoCusto);
      const venda = parseFloat(precoVenda);
      
      if (custo > 0) {
        const margem = ((venda - custo) / custo * 100).toFixed(2);
        setMargemLucro(margem);
      }
    }
  }, [precoCusto, precoVenda]);

  const escolherImagem = async () => {
    try {
      const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert('Permiss√£o necess√°ria', 'Precisamos de acesso √† galeria para selecionar imagens.');
        return;
      }

      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [1, 1],
        quality: 0.8,
      });

      if (!result.canceled && result.assets.length > 0) {
        setImagem(result.assets[0]);
        console.log('Imagem selecionada:', result.assets[0].uri);
      }
    } catch (error) {
      console.error('Erro ao selecionar imagem:', error);
      Alert.alert('Erro', 'N√£o foi poss√≠vel selecionar a imagem');
    }
  };

  const validarCampos = () => {
    if (!nome.trim()) {
      Alert.alert('Aten√ß√£o', 'O nome do produto √© obrigat√≥rio!');
      return false;
    }

    if (!precoCusto || isNaN(parseFloat(precoCusto)) || parseFloat(precoCusto) <= 0) {
      Alert.alert('Aten√ß√£o', 'Informe um pre√ßo de custo v√°lido!');
      return false;
    }

    if (!precoVenda || isNaN(parseFloat(precoVenda)) || parseFloat(precoVenda) <= 0) {
      Alert.alert('Aten√ß√£o', 'Informe um pre√ßo de venda v√°lido!');
      return false;
    }

    if (parseFloat(precoVenda) <= parseFloat(precoCusto)) {
      Alert.alert('Aten√ß√£o', 'O pre√ßo de venda deve ser maior que o pre√ßo de custo!');
      return false;
    }

    return true;
  };

  const salvarProduto = async () => {
    if (!validarCampos()) {
      return;
    }

    setLoading(true);

    try {
      const formData = new FormData();
      
      formData.append('nome', nome.trim());
      formData.append('preco_custo', parseFloat(precoCusto).toFixed(2));
      formData.append('preco_venda', parseFloat(precoVenda).toFixed(2));
      formData.append('observacoes', observacoes.trim());
      formData.append('estoque_atual', estoqueAtual ? parseInt(estoqueAtual).toString() : '0');

      if (imagem && imagem.uri) {
        const imageUri = Platform.OS === 'ios' ? imagem.uri.replace('file://', '') : imagem.uri;
        
        formData.append('imagem', {
          uri: imageUri,
          type: imagem.type || 'image/jpeg',
          name: imagem.uri.split('/').pop() || `produto_${Date.now()}.jpg`,
        } as any);
      }

      const url = isEditing 
        ? `http://localhost:3000/api/produtos/${produto.id}`
        : 'http://localhost:3000/api/produtos';
      
      const method = isEditing ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        body: formData,
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const result = await response.json();

      if (result.success) {
        Alert.alert(
          'Sucesso',
          isEditing ? 'Produto atualizado com sucesso!' : 'Produto cadastrado com sucesso!',
          [
            {
              text: 'OK',
              onPress: () => {
                navigation.navigate('Products', { reload: true });
              }
            }
          ]
        );
      } else {
        throw new Error(result.error || 'Erro desconhecido');
      }

    } catch (error: any) {
      console.error('Erro ao salvar produto:', error);
      Alert.alert('Erro', error.message || 'N√£o foi poss√≠vel salvar o produto');
    } finally {
      setLoading(false);
    }
  };

  const imagemParaExibir = imagem ? imagem.uri : imagemExistente;

  return (
    <ScrollView style={styles.container} showsVerticalScrollIndicator={false}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity 
          style={styles.backButton}
          onPress={() => navigation.goBack()}
        >
          <Text style={styles.backButtonText}>‚Üê Voltar</Text>
        </TouchableOpacity>
        
        <Text style={styles.title}>
          {isEditing ? 'Editar Produto' : 'Novo Produto'}
        </Text>

        {__DEV__ && (
          <TouchableOpacity 
            style={styles.debugButton}
            onPress={() => Alert.alert('Debug', 'Teste r√°pido de backend aqui')}
          >
            <Text style={styles.debugButtonText}>üîß</Text>
          </TouchableOpacity>
        )}
      </View>

      <View style={styles.form}>
        {/* Nome */}
        <View style={styles.inputGroup}>
          <Text style={styles.label}>Nome do Produto *</Text>
          <TextInput
            placeholder="Ex: Smartphone Samsung Galaxy"
            value={nome}
            onChangeText={setNome}
            style={styles.input}
            placeholderTextColor="#999"
          />
        </View>

        {/* Pre√ßos */}
        <View style={styles.row}>
          <View style={[styles.inputGroup, styles.halfWidth]}>
            <Text style={styles.label}>Pre√ßo de Custo *</Text>
            <TextInput
              placeholder="0,00"
              value={precoCusto}
              onChangeText={setPrecoCusto}
              keyboardType="numeric"
              style={styles.input}
              placeholderTextColor="#999"
            />
          </View>

          <View style={[styles.inputGroup, styles.halfWidth]}>
            <Text style={styles.label}>Pre√ßo de Venda *</Text>
            <TextInput
              placeholder="0,00"
              value={precoVenda}
              onChangeText={setPrecoVenda}
              keyboardType="numeric"
              style={styles.input}
              placeholderTextColor="#999"
            />
          </View>
        </View>

        {/* Margem de Lucro */}
        {margemLucro !== '0' && (
          <View style={styles.margemContainer}>
            <Text style={styles.margemText}>
              Margem de Lucro: {margemLucro}%
            </Text>
          </View>
        )}

        {/* Estoque */}
        <View style={styles.inputGroup}>
          <Text style={styles.label}>Estoque Atual</Text>
          <TextInput
            placeholder="0"
            value={estoqueAtual}
            onChangeText={setEstoqueAtual}
            keyboardType="numeric"
            style={styles.input}
            placeholderTextColor="#999"
          />
        </View>

        {/* Observa√ß√µes */}
        <View style={styles.inputGroup}>
          <Text style={styles.label}>Observa√ß√µes</Text>
          <TextInput
            placeholder="Informa√ß√µes adicionais sobre o produto..."
            value={observacoes}
            onChangeText={setObservacoes}
            style={[styles.input, styles.textArea]}
            multiline={true}
            numberOfLines={3}
            textAlignVertical="top"
            placeholderTextColor="#999"
          />
        </View>

        {/* Imagem */}
        <View style={styles.inputGroup}>
          <Text style={styles.label}>Imagem do Produto</Text>
          <TouchableOpacity
            style={styles.imageButton}
            onPress={escolherImagem}
          >
            <Text style={styles.imageButtonText}>
              {imagemParaExibir ? 'Alterar Imagem' : 'Selecionar Imagem'}
            </Text>
          </TouchableOpacity>

          {imagemParaExibir && (
            <View style={styles.imageContainer}>
              <Image
                source={{ 
                  uri: imagemParaExibir.startsWith('http') 
                    ? imagemParaExibir 
                    : `http://localhost:3000${imagemParaExibir}` 
                }}
                style={styles.image}
                resizeMode="cover"
              />
            </View>
          )}
        </View>

        {/* Bot√£o Salvar */}
        <TouchableOpacity
          style={[styles.saveButton, loading && styles.saveButtonDisabled]}
          onPress={salvarProduto}
          disabled={loading}
        >
          {loading ? (
            <ActivityIndicator size="small" color="#fff" />
          ) : (
            <Text style={styles.saveButtonText}>
              {isEditing ? 'Atualizar Produto' : 'Salvar Produto'}
            </Text>
          )}
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}

