import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
  RefreshControl,
} from 'react-native';
import { useFocusEffect } from '@react-navigation/native';

interface DashboardData {
  vendasMes: number;
  produtos: number;
  lucroMes: number;
  pendentes: number;
}

export default function DashboardScreen({ navigation }: any) {
  const [data, setData] = useState<DashboardData>({
    vendasMes: 0,
    produtos: 0,
    lucroMes: 0,
    pendentes: 0,
  });
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);

  const loadDashboardData = async (showLoading = true) => {
    try {
      if (showLoading) setLoading(true);

      console.log('üìä Carregando dados do dashboard...');

      const response = await fetch('http://localhost:3000/api/dashboard/summary');
      if (!response.ok) throw new Error(`HTTP ${response.status}`);

      const result = await response.json();
      console.log('üìä Dados recebidos:', result);

      if (result && !result.error) {
        setData({
          vendasMes: result.vendasMes || 0,
          produtos: result.produtos || 0,
          lucroMes: result.lucroMes || 0,
          pendentes: result.pendentes || 0,
        });
        console.log('‚úÖ Dashboard atualizado com sucesso');
      } else {
        throw new Error(result.error || 'Resposta inv√°lida do servidor');
      }

    } catch (error: any) {
      console.error('‚ùå Erro no dashboard:', error);
      Alert.alert(
        'Erro de Conex√£o',
        'N√£o foi poss√≠vel carregar os dados do dashboard.\n\nErro: ' + error.message,
        [{ text: 'OK' }]
      );
    } finally {
      if (showLoading) setLoading(false);
      setRefreshing(false);
    }
  };

  // Carregar ao entrar na tela
  useFocusEffect(
    React.useCallback(() => {
      loadDashboardData();
    }, [])
  );

  const onRefresh = () => {
    setRefreshing(true);
    loadDashboardData(false);
  };

  const formatCurrency = (value: number) =>
    new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(value);

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#2196F3" />
        <Text style={styles.loadingText}>Carregando dashboard...</Text>
      </View>
    );
  }

  return (
    <ScrollView
      style={styles.container}
      refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
    >
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.title}>GEV App</Text>
        <Text style={styles.subtitle}>Gest√£o Empresarial de Vendas</Text>
      </View>

      {/* Cards */}
      <View style={styles.cardsContainer}>
        <View style={styles.row}>
          <View style={[styles.card, styles.cardPrimary]}>
            <Text style={styles.cardValue}>{data.vendasMes}</Text>
            <Text style={styles.cardLabel}>Vendas do M√™s</Text>
          </View>

          <View style={[styles.card, styles.cardSuccess]}>
            <Text style={styles.cardValue}>{formatCurrency(data.lucroMes)}</Text>
            <Text style={styles.cardLabel}>Lucro do M√™s</Text>
          </View>
        </View>

        <View style={styles.row}>
          <View style={[styles.card, styles.cardInfo]}>
            <Text style={styles.cardValue}>{data.produtos}</Text>
            <Text style={styles.cardLabel}>Produtos Cadastrados</Text>
          </View>

          <View style={[styles.card, styles.cardWarning]}>
            <Text style={styles.cardValue}>{data.pendentes}</Text>
            <Text style={styles.cardLabel}>Vendas Pendentes</Text>
          </View>
        </View>
      </View>

      {/* Menu */}
      <View style={styles.menuContainer}>
        <Text style={styles.menuTitle}>Menu Principal</Text>
        <View style={styles.menuGrid}>
          <TouchableOpacity style={styles.menuItem} onPress={() => navigation.navigate('Products')}>
            <Text style={styles.menuIcon}>üì¶</Text>
            <Text style={styles.menuText}>Produtos</Text>
            <Text style={styles.menuSubtext}>Gerenciar estoque</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.menuItem} onPress={() => navigation.navigate('NewSale')}>
            <Text style={styles.menuIcon}>üí∞</Text>
            <Text style={styles.menuText}>Nova Venda</Text>
            <Text style={styles.menuSubtext}>Registrar venda</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.menuItem}
            onPress={() => navigation.navigate('SalesHistory')}
          >
            <Text style={styles.menuIcon}>üìä</Text>
            <Text style={styles.menuText}>Hist√≥rico</Text>
            <Text style={styles.menuSubtext}>Ver vendas</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.menuItem} onPress={() => navigation.navigate('Reports')}>
            <Text style={styles.menuIcon}>üìà</Text>
            <Text style={styles.menuText}>Relat√≥rios</Text>
            <Text style={styles.menuSubtext}>An√°lises</Text>
          </TouchableOpacity>
        </View>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#f5f5f5' },
  loadingContainer: { flex: 1, justifyContent: 'center', alignItems: 'center' },
  loadingText: { marginTop: 10, color: '#666' },
  header: {
    backgroundColor: '#2196F3',
    paddingTop: 50,
    paddingBottom: 30,
    alignItems: 'center',
  },
  title: { fontSize: 28, fontWeight: 'bold', color: 'white' },
  subtitle: { fontSize: 16, color: 'white', opacity: 0.9 },
  cardsContainer: { padding: 20 },
  row: { flexDirection: 'row', justifyContent: 'space-between', marginBottom: 15 },
  card: {
    width: '48%',
    padding: 20,
    borderRadius: 12,
  },
  cardPrimary: { backgroundColor: '#2196F3' },
  cardSuccess: { backgroundColor: '#4CAF50' },
  cardInfo: { backgroundColor: '#FF9800' },
  cardWarning: { backgroundColor: '#F44336' },
  cardValue: { fontSize: 18, fontWeight: 'bold', color: 'white' },
  cardLabel: { fontSize: 12, color: 'white' },
  menuContainer: { padding: 20 },
  menuTitle: { fontSize: 20, fontWeight: 'bold', marginBottom: 20 },
  menuGrid: { flexDirection: 'row', flexWrap: 'wrap', justifyContent: 'space-between' },
  menuItem: {
    width: '48%',
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 12,
    alignItems: 'center',
    marginBottom: 15,
  },
  menuIcon: { fontSize: 32, marginBottom: 10 },
  menuText: { fontSize: 16, fontWeight: '600' },
  menuSubtext: { fontSize: 12, color: '#666' },
});
