import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity, ActivityIndicator, Alert, Dimensions } from 'react-native';
import { useFocusEffect } from '@react-navigation/native';
import { BarChart } from 'react-native-chart-kit';

interface DashboardData {
  vendas_mes: number;
  total_produtos: number;
  lucro_mes: number;
  vendas_pendentes: number;
}

export default function DashboardScreen({ navigation }: any) {
  const [data, setData] = useState<DashboardData>({
    vendas_mes: 0,
    total_produtos: 0,
    lucro_mes: 0,
    vendas_pendentes: 0,
  });
  const [loading, setLoading] = useState(true);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:3000/api/dashboard/summary');
      if (!response.ok) throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      const result = await response.json();

      setData({
        vendas_mes: result.vendasMes || 0,
        total_produtos: result.produtos || 0,
        lucro_mes: result.lucroMes || 0,
        vendas_pendentes: result.pendentes || 0,
      });
    } catch (error: any) {
      console.error('Erro ao carregar dashboard:', error);
      Alert.alert('Erro', 'NÃ£o foi possÃ­vel carregar os dados do dashboard.\n' + error.message);
    } finally {
      setLoading(false);
    }
  };

  useFocusEffect(
    React.useCallback(() => {
      loadDashboardData();
    }, [])
  );

  const formatCurrency = (value: number) =>
    new Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(value);

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#2196F3" />
        <Text style={styles.loadingText}>Carregando dashboard...</Text>
      </View>
    );
  }

  return (
    <ScrollView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.title}>GEV App</Text>
        <Text style={styles.subtitle}>GestÃ£o Empresarial de Vendas</Text>
      </View>

      {/* Cards de mÃ©tricas */}
      <View style={styles.cardsContainer}>
        <View style={styles.row}>
          <View style={[styles.card, styles.cardPrimary]}>
            <Text style={styles.cardValue}>{formatCurrency(data.vendas_mes)}</Text>
            <Text style={styles.cardLabel}>Vendas do MÃªs</Text>
          </View>

          <View style={[styles.card, styles.cardSuccess]}>
            <Text style={styles.cardValue}>{formatCurrency(data.lucro_mes)}</Text>
            <Text style={styles.cardLabel}>Lucro do MÃªs</Text>
          </View>
        </View>

        <View style={styles.row}>
          <View style={[styles.card, styles.cardInfo]}>
            <Text style={styles.cardValue}>{data.total_produtos}</Text>
            <Text style={styles.cardLabel}>Produtos Cadastrados</Text>
          </View>

          <View style={[styles.card, styles.cardWarning]}>
            <Text style={styles.cardValue}>{data.vendas_pendentes}</Text>
            <Text style={styles.cardLabel}>Vendas Pendentes</Text>
          </View>
        </View>
      </View>

      {/* GrÃ¡fico Vendas vs Lucro */}
      <View style={styles.chartContainer}>
        <Text style={styles.chartTitle}>Vendas vs Lucro</Text>
        <BarChart
          data={{
            labels: ['Vendas', 'Lucro'],
            datasets: [{ data: [data.vendas_mes, data.lucro_mes] }],
          }}
          width={Dimensions.get('window').width - 40}
          height={220}
          fromZero
          chartConfig={{
            backgroundGradientFrom: '#fff',
            backgroundGradientTo: '#fff',
            decimalPlaces: 2,
            color: (opacity = 1) => `rgba(33, 150, 243, ${opacity})`,
            labelColor: (opacity = 1) => `rgba(0,0,0,${opacity})`,
          }}
          style={{ borderRadius: 12 }}
        />
      </View>

      {/* BotÃ£o refresh */}
      <View style={styles.refreshContainer}>
        <TouchableOpacity style={styles.refreshButton} onPress={loadDashboardData}>
          <Text style={styles.refreshButtonText}>ðŸ”„ Recarregar Dados</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#f5f5f5' },
  loadingContainer: { flex: 1, justifyContent: 'center', alignItems: 'center', backgroundColor: '#f5f5f5' },
  loadingText: { marginTop: 10, color: '#666', fontSize: 16 },
  header: { backgroundColor: '#2196F3', paddingVertical: 30, alignItems: 'center' },
  title: { fontSize: 28, fontWeight: 'bold', color: 'white' },
  subtitle: { fontSize: 16, color: 'white', opacity: 0.9 },
  cardsContainer: { padding: 20 },
  row: { flexDirection: 'row', justifyContent: 'space-between', marginBottom: 15 },
  card: { width: '48%', padding: 20, borderRadius: 12, elevation: 3, shadowColor: '#000', shadowOffset: { width: 0, height: 2 }, shadowOpacity: 0.1, shadowRadius: 4 },
  cardPrimary: { backgroundColor: '#2196F3' },
  cardSuccess: { backgroundColor: '#4CAF50' },
  cardInfo: { backgroundColor: '#FF9800' },
  cardWarning: { backgroundColor: '#F44336' },
  cardValue: { fontSize: 18, fontWeight: 'bold', color: 'white', marginBottom: 5 },
  cardLabel: { fontSize: 12, color: 'white', opacity: 0.9 },
  chartContainer: { paddingHorizontal: 20, marginTop: 10, marginBottom: 20 },
  chartTitle: { fontSize: 16, fontWeight: 'bold', marginBottom: 10, textAlign: 'center' },
  refreshContainer: { paddingHorizontal: 20, marginBottom: 20 },
  refreshButton: { padding: 12, backgroundColor: '#2196F3', borderRadius: 8, alignItems: 'center' },
  refreshButtonText: { color: '#fff', fontWeight: 'bold' },
});
