import React, { useState } from "react";
import {
  View, Text, TextInput, TouchableOpacity, Alert, Image,
  StyleSheet, ScrollView, Platform, ActivityIndicator
} from "react-native";
import * as ImagePicker from "expo-image-picker";
import { addProduto } from "../api";

export default function NewProductScreen({ navigation }: any) {
  const [nome, setNome] = useState("");
  const [precoCusto, setPrecoCusto] = useState("");
  const [precoVenda, setPrecoVenda] = useState("");
  const [estoqueAtual, setEstoqueAtual] = useState("0");
  const [imagem, setImagem] = useState<any>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const escolherImagem = async () => {
    try {
      const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
      if (status !== "granted") {
        Alert.alert("PermissÃ£o necessÃ¡ria", "Permita o acesso Ã  galeria.");
        return;
      }
      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        quality: 0.7,
        allowsEditing: true,
      });
      if (!result.canceled) setImagem(result.assets[0]);
    } catch (err) {
      console.error("Erro ao escolher imagem:", err);
      Alert.alert("Erro", "NÃ£o foi possÃ­vel selecionar a imagem.");
    }
  };

  const salvarProduto = async () => {
    if (!nome.trim() || !precoCusto.trim() || !precoVenda.trim()) {
      Alert.alert("AtenÃ§Ã£o", "Preencha todos os campos obrigatÃ³rios!");
      return;
    }

    const estoqueNum = parseInt(estoqueAtual) || 0;
    setIsSubmitting(true);

    try {
      const formData = new FormData();
      formData.append("nome", nome.trim());
      formData.append("preco_custo", precoCusto.trim());
      formData.append("preco_venda", precoVenda.trim());
      formData.append("estoque_atual", estoqueNum.toString());

      if (imagem?.uri) {
        const localUri = imagem.uri;
        const filename = localUri.split("/").pop() || `photo_${Date.now()}.jpg`;
        const match = /\.(\w+)$/.exec(filename);
        const mimeType = match ? `image/${match[1]}` : "image/jpeg";
        const uriForUpload = Platform.OS === "android" ? localUri : localUri.replace("file://", "");
        formData.append("imagem", { uri: uriForUpload, name: filename, type: mimeType } as any);
      }

      const res = await addProduto(formData);

      if (res?.data?.success) {
        Alert.alert("Sucesso", "Produto cadastrado com sucesso!");
        navigation.navigate("ProductsScreen", { reload: true });
      } else {
        const serverMsg = res?.data?.error || res?.data?.message || JSON.stringify(res?.data);
        Alert.alert("Erro", serverMsg);
      }
    } catch (error: any) {
      console.error("Erro ao cadastrar produto:", error);
      Alert.alert("Erro", error.message || "Erro desconhecido");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.title}>Novo Produto</Text>

      <TextInput placeholder="Nome do produto" value={nome} onChangeText={setNome} style={styles.input} editable={!isSubmitting} />
      <TextInput placeholder="PreÃ§o de custo" value={precoCusto} onChangeText={setPrecoCusto} keyboardType="decimal-pad" style={styles.input} editable={!isSubmitting} />
      <TextInput placeholder="PreÃ§o de venda" value={precoVenda} onChangeText={setPrecoVenda} keyboardType="decimal-pad" style={styles.input} editable={!isSubmitting} />
      <TextInput placeholder="Estoque atual" value={estoqueAtual} onChangeText={setEstoqueAtual} keyboardType="number-pad" style={styles.input} editable={!isSubmitting} />

      <TouchableOpacity style={styles.imageButton} onPress={escolherImagem} disabled={isSubmitting}>
        <Text style={styles.imageButtonText}>{imagem ? "ðŸ“¸ Alterar Imagem" : "ðŸ“¸ Escolher Imagem"}</Text>
      </TouchableOpacity>

      {imagem && <Image source={{ uri: imagem.uri }} style={styles.previewImage} />}

      <TouchableOpacity style={[styles.saveButton, isSubmitting && { opacity: 0.7 }]} onPress={salvarProduto} disabled={isSubmitting}>
        {isSubmitting ? <ActivityIndicator color="#fff" /> : <Text style={styles.saveButtonText}>ðŸ’¾ Salvar Produto</Text>}
      </TouchableOpacity>

      <TouchableOpacity style={styles.backButton} onPress={() => navigation.goBack()} disabled={isSubmitting}>
        <Text style={styles.backButtonText}>â¬… Voltar</Text>
      </TouchableOpacity>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: { flexGrow: 1, padding: 20, backgroundColor: "#f5f5f5", alignItems: "center" },
  title: { fontSize: 22, fontWeight: "bold", color: "#2196F3", marginBottom: 20 },
  input: { width: "100%", borderBottomWidth: 1, borderBottomColor: "#ccc", paddingVertical: 10, paddingHorizontal: 6, marginBottom: 15, fontSize: 16 },
  imageButton: { backgroundColor: "#2196F3", borderRadius: 8, padding: 12, marginBottom: 15, width: "100%", alignItems: "center" },
  imageButtonText: { color: "#fff", fontWeight: "600", fontSize: 16 },
  previewImage: { width: 200, height: 200, borderRadius: 12, marginBottom: 20 },
  saveButton: { backgroundColor: "#4CAF50", borderRadius: 8, padding: 15, marginBottom: 15, width: "100%", alignItems: "center" },
  saveButtonText: { color: "#fff", fontWeight: "700", fontSize: 16 },
  backButton: { backgroundColor: "#ddd", borderRadius: 8, padding: 12, width: "100%", alignItems: "center" },
  backButtonText: { color: "#333", fontSize: 16, fontWeight: "500" },
});
