import { useState } from "react";
import { View, TextInput, Button, Alert, StyleSheet } from "react-native";
import { addVenda } from "../api";

export default function NewSaleScreen({ navigation }: any) {
  const [cliente, setCliente] = useState("");
  const [total, setTotal] = useState("");
  const [loading, setLoading] = useState(false);

  const salvarVenda = async () => {
    if (!cliente.trim() || !total.trim()) {
      Alert.alert("Atenção", "Preencha todos os campos!");
      return;
    }

    const valor = parseFloat(total.replace(",", "."));
    if (isNaN(valor) || valor <= 0) {
      Alert.alert("Erro", "Digite um valor válido para a venda.");
      return;
    }

    try {
      setLoading(true);
      console.log("Enviando venda:", { cliente_nome: cliente, total: valor });
      
      const res = await addVenda({ cliente_nome: cliente, total: valor });
      console.log("Resposta do servidor:", res.data);
      
      Alert.alert("Sucesso", "Venda registrada com sucesso!");
      navigation.navigate("Dashboard"); // volta para o Dashboard
    } catch (err: any) {
      console.error("Erro ao registrar venda:", err.response?.data || err.message);
      Alert.alert(
        "Erro", 
        "Não foi possível registrar a venda.\nVerifique a conexão com o backend e se o IP está correto."
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <View style={styles.container}>
      <TextInput
        placeholder="Nome do Cliente"
        value={cliente}
        onChangeText={setCliente}
        style={styles.input}
      />
      <TextInput
        placeholder="Valor total da venda"
        value={total}
        onChangeText={setTotal}
        keyboardType="numeric"
        style={styles.input}
      />

      <View style={{ marginBottom: 10 }}>
        <Button
          title={loading ? "Registrando..." : "Registrar Venda"}
          onPress={salvarVenda}
          disabled={loading}
        />
      </View>

      <Button
        title="⬅ Voltar para Dashboard"
        onPress={() => navigation.navigate("Dashboard")}
        color="#888"
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    padding: 20,
    flex: 1,
    justifyContent: "center",
  },
  input: {
    marginBottom: 15,
    borderBottomWidth: 1,
    paddingVertical: 8,
    paddingHorizontal: 5,
  },
});
