import React, { useState, useCallback } from "react";
import { 
  View, 
  FlatList, 
  ActivityIndicator, 
  Alert, 
  StyleSheet,
  Text,
  TouchableOpacity,
  TextInput
} from "react-native";
import { useFocusEffect } from "@react-navigation/native";
import { getProdutos } from "../api";
import ProductItem from "../components/ProductItem";

export default function ProductsScreen({ navigation }: any) {
  const [produtos, setProdutos] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");

  const carregarProdutos = async (searchTerm?: string) => {
    try {
      setLoading(true);
      const response = await getProdutos(searchTerm);
      setProdutos(response.data.data);
    } catch (error) {
      Alert.alert("Erro", "NÃ£o foi possÃ­vel carregar os produtos");
    } finally {
      setLoading(false);
    }
  };

  // Recarregar sempre que a tela voltar ao foco
  useFocusEffect(
    useCallback(() => {
      carregarProdutos();
    }, [])
  );

  const handleSearch = (text: string) => {
    setSearch(text);
    if (text.length > 2 || text.length === 0) {
      carregarProdutos(text);
    }
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#2196F3" />
        <Text style={styles.loadingText}>Carregando produtos...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Estoque Inteligente</Text>
        <Text style={styles.subtitle}>{produtos.length} produtos cadastrados</Text>
      </View>

      <View style={styles.searchContainer}>
        <TextInput
          style={styles.searchInput}
          placeholder="ðŸ” Buscar produtos..."
          value={search}
          onChangeText={handleSearch}
        />
      </View>
      
      <FlatList
        data={produtos}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <ProductItem 
            produto={item} 
            onPress={() => navigation.navigate("NewProduct", { produto: item })}
          />
        )}
        contentContainerStyle={styles.listContainer}
        showsVerticalScrollIndicator={false}
      />

      <TouchableOpacity
        style={styles.floatingButton}
        onPress={() => navigation.navigate("NewProduct")}
      >
        <Text style={styles.floatingButtonText}>+</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#f5f5f5' },
  loadingContainer: { flex: 1, justifyContent: 'center', alignItems: 'center' },
  loadingText: { marginTop: 10, color: '#666' },
  header: { backgroundColor: '#2196F3', paddingTop: 50, paddingBottom: 20, paddingHorizontal: 20, alignItems: 'center' },
  title: { fontSize: 24, fontWeight: 'bold', color: 'white', marginBottom: 5 },
  subtitle: { fontSize: 14, color: 'white', opacity: 0.9 },
  searchContainer: { padding: 20 },
  searchInput: { backgroundColor: 'white', borderRadius: 25, paddingHorizontal: 20, paddingVertical: 12, fontSize: 16, elevation: 2, shadowColor: '#000', shadowOffset: { width: 0, height: 1 }, shadowOpacity: 0.22, shadowRadius: 2.22 },
  listContainer: { paddingHorizontal: 20, paddingBottom: 100 },
  floatingButton: { position: 'absolute', bottom: 30, right: 30, width: 60, height: 60, backgroundColor: '#FF9800', borderRadius: 30, justifyContent: 'center', alignItems: 'center', elevation: 5, shadowColor: '#000', shadowOffset: { width: 0, height: 2 }, shadowOpacity: 0.25, shadowRadius: 3.84 },
  floatingButtonText: { color: 'white', fontSize: 28, fontWeight: 'bold' },
});
