import React, { useEffect, useState } from "react";
import { 
  View, Text, FlatList, TouchableOpacity, StyleSheet, ActivityIndicator, Alert 
} from "react-native";
import { getProdutos } from "../api";

export default function ProductsScreen({ navigation, route }: any) {
  const [produtos, setProdutos] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);

  const fetchProdutos = async () => {
    setLoading(true);
    try {
      const res = await getProdutos();
      if (res?.data?.success) {
        setProdutos(res.data.data);
      } else {
        Alert.alert("Erro", "Não foi possível carregar os produtos");
      }
    } catch (error) {
      console.error("Erro ao buscar produtos:", error);
      Alert.alert("Erro", "Falha ao conectar com o backend");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProdutos();
  }, []);

  // Atualiza a lista quando volta da tela de cadastro
  useEffect(() => {
    if (route?.params?.reload) {
      fetchProdutos();
      navigation.setParams({ reload: false }); // limpa o parâmetro
    }
  }, [route?.params?.reload]);

  const renderItem = ({ item }: any) => (
    <View style={styles.item}>
      <Text style={styles.itemTitle}>{item.nome}</Text>
      <Text>Preço de venda: R$ {item.preco_venda}</Text>
      <Text>Estoque: {item.estoque_atual}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <TouchableOpacity 
        style={styles.addButton} 
        onPress={() => navigation.navigate("NewProductScreen")}
      >
        <Text style={styles.addButtonText}>➕ Novo Produto</Text>
      </TouchableOpacity>

      {loading ? (
        <ActivityIndicator size="large" color="#2196F3" style={{ marginTop: 30 }} />
      ) : (
        <FlatList
          data={produtos}
          keyExtractor={(item) => item.id?.toString() || Math.random().toString()}
          renderItem={renderItem}
          contentContainerStyle={{ paddingBottom: 20 }}
        />
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, padding: 20, backgroundColor: "#f5f5f5" },
  addButton: {
    backgroundColor: "#2196F3",
    padding: 15,
    borderRadius: 8,
    marginBottom: 15,
    alignItems: "center",
  },
  addButtonText: { color: "#fff", fontSize: 16, fontWeight: "600" },
  item: {
    backgroundColor: "#fff",
    padding: 15,
    borderRadius: 8,
    marginBottom: 10,
    elevation: 2,
  },
  itemTitle: { fontWeight: "700", fontSize: 16, marginBottom: 5 },
});
