import React, { useState, useCallback, useEffect } from 'react';
import {
  View,
  FlatList,
  ActivityIndicator,
  Alert,
  StyleSheet,
  Text,
  TouchableOpacity,
  TextInput,
  RefreshControl,
} from 'react-native';
import { useFocusEffect, useRoute, RouteProp } from '@react-navigation/native';

interface Produto {
  id: number;
  nome: string;
  preco_custo: number;
  preco_venda: number;
  margem_lucro: number;
  estoque_atual: number;
  imagem?: string;
  observacoes?: string;
}

type RootStackParamList = {
  Products: { reload?: boolean } | undefined;
};

type ProductsScreenRouteProp = RouteProp<RootStackParamList, 'Products'>;

const ProductItem = ({ produto, onPress }: { produto: Produto; onPress: () => void }) => {
  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(value);
  };

  const getEstoqueColor = (estoque: number) => {
    if (estoque === 0) return '#f44336';
    if (estoque <= 10) return '#ff9800';
    return '#4caf50';
  };

  return (
    <TouchableOpacity style={styles.productItem} onPress={onPress} activeOpacity={0.7}>
      <View style={styles.productInfo}>
        <Text style={styles.productName} numberOfLines={2}>
          {produto.nome}
        </Text>

        <View style={styles.pricesContainer}>
          <Text style={styles.precoCusto}>
            Custo: {formatCurrency(produto.preco_custo)}
          </Text>
          <Text style={styles.precoVenda}>
            Venda: {formatCurrency(produto.preco_venda)}
          </Text>
        </View>

        <View style={styles.bottomRow}>
          <View style={styles.margemContainer}>
            <Text style={styles.margemText}>
              {produto.margem_lucro.toFixed(1)}% lucro
            </Text>
          </View>

          <View style={[styles.estoqueContainer, { backgroundColor: getEstoqueColor(produto.estoque_atual) }]}>
            <Text style={styles.estoqueText}>
              {produto.estoque_atual} un.
            </Text>
          </View>
        </View>
      </View>

      <View style={styles.editIndicator}>
        <Text style={styles.editIcon}>✏️</Text>
      </View>
    </TouchableOpacity>
  );
};

export default function ProductsScreen({ navigation }: any) {
  const [produtos, setProdutos] = useState<Produto[]>([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [search, setSearch] = useState('');

  const route = useRoute<ProductsScreenRouteProp>();

  const carregarProdutos = async (searchTerm?: string, showLoading = true) => {
    try {
      if (showLoading) {
        setLoading(true);
      }

      const url = searchTerm 
        ? `http://localhost:3000/api/produtos?search=${encodeURIComponent(searchTerm)}`
        : 'http://localhost:3000/api/produtos';

      const response = await fetch(url);
      const result = await response.json();

      if (result.success) {
        setProdutos(result.data);
      } else {
        throw new Error(result.error || 'Erro ao carregar produtos');
      }

    } catch (error: any) {
      console.error('Erro ao carregar produtos:', error);
      Alert.alert(
        'Erro',
        'Não foi possível carregar os produtos. Verifique sua conexão.'
      );
    } finally {
      if (showLoading) {
        setLoading(false);
      }
    }
  };

  const onRefresh = useCallback(async () => {
    setRefreshing(true);
    await carregarProdutos(search, false);
    setRefreshing(false);
  }, [search]);

  useFocusEffect(
    useCallback(() => {
      carregarProdutos();
    }, [])
  );

  useEffect(() => {
    if (route.params?.reload) {
      carregarProdutos();
      navigation.setParams({ reload: undefined });
    }
  }, [route.params?.reload]);

  const handleSearch = useCallback((text: string) => {
    setSearch(text);
    if (text.length > 2 || text.length === 0) {
      const timeoutId = setTimeout(() => {
        carregarProdutos(text);
      }, 500);
      return () => clearTimeout(timeoutId);
    }
  }, []);

  const handleProductPress = (produto: Produto) => {
    navigation.navigate('NewProduct', { produto });
  };

  const handleAddProduct = () => {
    navigation.navigate('NewProduct');
  };

  const renderEmptyState = () => (
    <View style={styles.emptyContainer}>
      <Text style={styles.emptyIcon}>📦</Text>
      <Text style={styles.emptyTitle}>
        {search ? 'Nenhum produto encontrado' : 'Nenhum produto cadastrado'}
      </Text>
      <Text style={styles.emptySubtitle}>
        {search 
          ? 'Tente buscar por outro termo' 
          : 'Comece cadastrando seu primeiro produto'
        }
      </Text>
      {!search && (
        <TouchableOpacity 
          style={styles.emptyButton}
          onPress={handleAddProduct}
        >
          <Text style={styles.emptyButtonText}>Cadastrar Produto</Text>
        </TouchableOpacity>
      )}
    </View>
  );

  if (loading) {
    return (
      <View style={styles.container}>
        <View style={styles.header}>
          <TouchableOpacity 
            style={styles.backButton}
            onPress={() => navigation.goBack()}
          >
            <Text style={styles.backButtonText}>← Voltar</Text>
          </TouchableOpacity>
          <Text style={styles.title}>Estoque Inteligente</Text>
        </View>
        
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#2196F3" />
          <Text style={styles.loadingText}>Carregando produtos...</Text>
        </View>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity 
          style={styles.backButton}
          onPress={() => navigation.goBack()}
        >
          <Text style={styles.backButtonText}>← Voltar</Text>
        </TouchableOpacity>
        
        <Text style={styles.title}>Estoque Inteligente</Text>
        <Text style={styles.subtitle}>
          {produtos.length} produto{produtos.length !== 1 ? 's' : ''} cadastrado{produtos.length !== 1 ? 's' : ''}
        </Text>
        
        {__DEV__ && (
          <TouchableOpacity 
            style={styles.debugButton}
            onPress={() => Alert.alert('Debug', 'Sistema funcionando!')}
          >
            <Text style={styles.debugButtonText}>🔧</Text>
          </TouchableOpacity>
        )}
      </View>

      {/* Busca */}
      <View style={styles.searchContainer}>
        <TextInput
          style={styles.searchInput}
          placeholder="🔍 Buscar produtos..."
          value={search}
          onChangeText={handleSearch}
          placeholderTextColor="#999"
          returnKeyType="search"
        />
      </View>
      
      {/* Lista */}
      <FlatList
        data={produtos}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <ProductItem 
            produto={item} 
            onPress={() => handleProductPress(item)}
          />
        )}
        contentContainerStyle={[
          styles.listContainer,
          produtos.length === 0 && styles.listContainerEmpty
        ]}
        showsVerticalScrollIndicator={false}
        refreshControl={
          <RefreshControl
            refreshing={refreshing}
            onRefresh={onRefresh}
            colors={['#2196F3']}
            tintColor="#2196F3"
          />
        }
        ListEmptyComponent={renderEmptyState}
      />

      {/* Botão flutuante para adicionar */}
      <TouchableOpacity
        style={styles.floatingButton}
        onPress={handleAddProduct}
        activeOpacity={0.8}
      >
        <Text style={styles.floatingButtonText}>+</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { 
    flex: 1, 
    backgroundColor: '#f5f5f5' 
  },
  loadingContainer: { 
    flex: 1, 
    justifyContent: 'center', 
    alignItems: 'center' 
  },
  loadingText: { 
    marginTop: 10, 
    color: '#666',
    fontSize: 16
  },
  header: { 
    backgroundColor: '#2196F3', 
    paddingTop: 50, 
    paddingBottom: 20, 
    paddingHorizontal: 20, 
    alignItems: 'center',
    position: 'relative'
  },
  backButton: { 
    position: 'absolute', 
    left: 20, 
    top: 55 
  },
  backButtonText: { 
    color: 'white', 
    fontSize: 16,
    fontWeight: '600'
  },
  debugButton: {
    position: 'absolute',
    right: 20,
    top: 55,
    width: 30,
    height: 30,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(255,255,255,0.2)',
    borderRadius: 15,
  },
  debugButtonText: {
    fontSize: 16,
  },
  title: { 
    fontSize: 24, 
    fontWeight: 'bold', 
    color: 'white', 
    marginBottom: 5 
  },
  subtitle: { 
    fontSize: 14, 
    color: 'white', 
    opacity: 0.9 
  },
  searchContainer: { 
    padding: 20 
  },
  searchInput: { 
    backgroundColor: 'white', 
    borderRadius: 25, 
    paddingHorizontal: 20, 
    paddingVertical: 12, 
    fontSize: 16, 
    elevation: 2, 
    shadowColor: '#000', 
    shadowOffset: { width: 0, height: 1 }, 
    shadowOpacity: 0.22, 
    shadowRadius: 2.22 
  },
  listContainer: { 
    paddingHorizontal: 20, 
